an&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;34&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;35&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;36&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;37&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;38&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;39&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;40&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;41&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;42&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;43&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;44&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;45&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;46&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;47&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;48&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;49&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;50&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;51&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs sql&#34;&gt;$&amp;#123;&lt;br&gt;  &amp;quot;SELECT&lt;br&gt;	\&amp;#x27;é˜³æ€§ç‡\&amp;#x27; type,&lt;br&gt;    \&amp;#x27;è°ƒæŸ¥æ—¶æ•ˆ\&amp;#x27; type1,&lt;br&gt;	tmp.investigator,&lt;br&gt;	tmp.real_name,&lt;br&gt;	tmp.totalNum,&lt;br&gt;	tmp.positiveNum,&lt;br&gt;	tmp.positiveNum / tmp.totalNum positiveRate,&lt;br&gt;	FORMAT( times / 86400000 / tmp.totalNum, 9 ) timeLimit &lt;br&gt;FROM&lt;br&gt;	(&lt;br&gt;	SELECT&lt;br&gt;		temp.investigator,&lt;br&gt;		temp.real_name,&lt;br&gt;		SUM( temp.num ) totalNum,&lt;br&gt;		SUM( times ) times,&lt;br&gt;		sum( CASE temp.positive WHEN \&amp;#x27;1\&amp;#x27; THEN temp.num ELSE 0 END ) positiveNum &lt;br&gt;	FROM&lt;br&gt;		(&lt;br&gt;		SELECT&lt;br&gt;			t.investigator,&lt;br&gt;			ai.real_name,&lt;br&gt;			atf.positive,&lt;br&gt;			COUNT( 1 ) num,&lt;br&gt;			sum( t.complete_time - t.accept_time ) times &lt;br&gt;		FROM&lt;br&gt;			ass_task t&lt;br&gt;			INNER JOIN ass_case ac ON ac.id = t.case_id&lt;br&gt;			INNER JOIN ass_task_feedback atf ON atf.task_id = t.id&lt;br&gt;			INNER JOIN ass_investigator ai ON ai.id = t.investigator &lt;br&gt;&lt;br&gt;		WHERE&lt;br&gt;			ac.status = \&amp;#x27;6\&amp;#x27;&amp;quot;&lt;br&gt;  			&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;(param(&amp;quot;orgId&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;null&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;&amp;amp;&amp;amp;&lt;/span&gt; param(&amp;quot;orgId&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&amp;quot;&amp;quot; ? &amp;quot; and ac.ass_company_id = \&amp;#x27;&amp;quot;&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;param(&amp;quot;orgId&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;&amp;quot;\&amp;#x27;&amp;quot;:&amp;quot; &amp;quot;)&lt;br&gt;  			&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;(param(&amp;quot;companyId&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;null&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;&amp;amp;&amp;amp;&lt;/span&gt; param(&amp;quot;companyId&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&amp;quot;&amp;quot; ? &amp;quot; and ac.company_id = \&amp;#x27;&amp;quot;&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;param(&amp;quot;companyId&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;&amp;quot;\&amp;#x27;&amp;quot;:&amp;quot; &amp;quot;)&lt;br&gt;  			&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;(param(&amp;quot;provinceCode&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;null&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;&amp;amp;&amp;amp;&lt;/span&gt; param(&amp;quot;provinceCode&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&amp;quot;&amp;quot; ? &amp;quot; and t.province_code = \&amp;#x27;&amp;quot;&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;param(&amp;quot;provinceCode&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;&amp;quot;\&amp;#x27;&amp;quot;:&amp;quot; &amp;quot;)&lt;br&gt;  			&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;(param(&amp;quot;cityCode&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;null&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;&amp;amp;&amp;amp;&lt;/span&gt; param(&amp;quot;cityCode&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&amp;quot;&amp;quot; ? &amp;quot; and t.city_code = \&amp;#x27;&amp;quot;&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;param(&amp;quot;cityCode&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;&amp;quot;\&amp;#x27;&amp;quot;:&amp;quot; &amp;quot;)&lt;br&gt;			&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;(param(&amp;quot;writeOffTime_GE&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;null&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;&amp;amp;&amp;amp;&lt;/span&gt; param(&amp;quot;writeOffTime_LE&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;!=&lt;/span&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;null&lt;/span&gt; ? &amp;quot; and ac.write_off_time &amp;gt;= &amp;quot;&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;param(&amp;quot;writeOffTime_GE&amp;quot;)&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;&amp;quot; and ac.write_off_time &amp;lt;= &amp;quot;&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;param(&amp;quot;writeOffTime_LE&amp;quot;):&amp;quot; &amp;quot;)&lt;br&gt;			&lt;span class=&#34;hljs-operator&#34;&gt;+&lt;/span&gt;&amp;quot; GROUP BY&lt;br&gt;			t.investigator,&lt;br&gt;			ai.real_name,&lt;br&gt;			atf.positive &lt;br&gt;		) temp &lt;br&gt;	GROUP BY&lt;br&gt;		temp.investigator,&lt;br&gt;		temp.real_name &lt;br&gt;	) tmp &lt;br&gt;ORDER BY&lt;br&gt;	tmp.totalNum desc&amp;quot;&lt;br&gt;&amp;#125;&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

