#34;language-bash&#34;&gt;判断镜像是否存在，如存在则删除&lt;/span&gt;&lt;br&gt;if sudo docker images --filter=reference=&amp;quot;$img:$tag&amp;quot; | grep -i &amp;quot;$img&amp;quot;; then&lt;br&gt;  echo &amp;quot;镜像: $img:$tag 镜像存在, 删除镜像重新构建.&amp;quot;&lt;br&gt;  sudo docker rmi $img:$tag&lt;br&gt;fi&lt;br&gt;&lt;span class=&#34;hljs-meta prompt_&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-meta prompt_&#34;&gt;# &lt;/span&gt;&lt;span class=&#34;language-bash&#34;&gt;构建新镜像&lt;/span&gt;&lt;br&gt;echo &amp;quot;构建镜像....................&amp;quot;&lt;br&gt;sudo docker build -t $img:$tag .&lt;br&gt;&lt;span class=&#34;hljs-meta prompt_&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-meta prompt_&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-meta prompt_&#34;&gt;# &lt;/span&gt;&lt;span class=&#34;language-bash&#34;&gt;启动服务，挂载外部数据信息&lt;/span&gt;&lt;br&gt;echo &amp;quot;启动容器服务!&amp;quot;&lt;br&gt;sudo docker run -d -p 8888:8080 --net=host --restart=always --name $container $img:$tag&lt;br&gt;&lt;span class=&#34;hljs-meta prompt_&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-meta prompt_&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-meta prompt_&#34;&gt;# &lt;/span&gt;&lt;span class=&#34;language-bash&#34;&gt;检测服务是否启动成功&lt;/span&gt;&lt;br&gt;if sudo docker ps --filter name=^/$container$ | grep -i &amp;quot;up&amp;quot;; then&lt;br&gt;    echo &amp;quot;服务启动成功....................&amp;quot;&lt;br&gt;else&lt;br&gt;    echo &amp;quot;服务启动失败....................&amp;quot;&lt;br&gt;fi&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

